class ConsoleIndex{
    constructor(botao1, botao2, botao3, botao4){
        this.botao1 = botao1,
        this.botao2 = botao2,
        this.botao3 = botao3,
        this.botao4 = botao4
    }

    get botao1(){
        return this.botao1;
    }
    get botao2(){
        return this.botao2;
    }
    get botao3(){
        return this.botao3;
    }
    get botao4(){
        return this.botao4;
    }
    set botao1(botao1){
        this.botao1 = botao1;
    }
    set botao2(botao2){
        this.botao2 = botao2;
    }
    set botao3(botao3){
        this.botao3 = botao3;
    }
    set botao4(botao4){
        this.botao4 = botao4;
    }
}